mapper.select:
SELECT u.funding_idx, m.mem_email, m.mem_name, CONCAT( DATE_FORMAT(p.funding_end_date + INTERVAL 1 DAY, '%Y-%m-%d'), ' ~ ', DATE_FORMAT(p.funding_end_date + INTERVAL 8 DAY, '%Y-%m-%d') ) AS user_payment_date, u.funding_pay_amt, pay.pro_pay_status, a.fintech_use_num, pay.pro_funding_pay_method_idx FROM funding_user u JOIN member m ON u.funding_mem_email = m.mem_email JOIN project_info p ON p.project_idx = u.funding_project_idx LEFT JOIN project_payment pay ON u.funding_idx = pay.pro_funding_idx LEFT JOIN user_account a ON m.mem_email = a.mem_email WHERE p.project_code = #{project_code} AND u.funding_status != '후원취소'
AND ( u.funding_idx like concat('%',#{searchKeyword},'%') OR m.mem_name like concat('%',#{searchKeyword},'%') OR m.mem_email like concat('%',#{searchKeyword},'%') )
ORDER BY p.project_idx DESC LIMIT #{startRow} , #{listLimit}
