mapper.insert:
INSERT
INTO member
VALUES (
	null
	,#{mem_email}
	,#{mem_name}
	,#{mem_passwd}
	,#{mem_post_code}
	,#{mem_add1}
	,#{mem_add2}
	,#{mem_tel}
	, 1                   -- sns수신
	,#{mem_birthday}
	, now()               -- 가입일자
	, null                -- 탈퇴일자
	, 1                   -- 회원상태(1 : 정상, 2 : 휴면, 3 : 탈퇴)
	, 'N'                 -- 메일인증여부
	, 0                   -- 관리자여부(0 : 일반, 1 : 관리자)
)

mapper.select:
SELECT *
FROM member
WHERE mem_email = #{mem_email}
OR mem_tel = #{mem_tel}
OR mem_name = #{mem_name}

SELECT *
FROM member
WHERE mem_email = #{mem_email}

SELECT *
FROM member
WHERE mem_email = #{mem_email}

mapper.update:
		UPDATE member
		SET 
			mem_passwd = #{mem_passwd}
		WHERE
			mem_tel = #{mem_tel}

mapper.select:
SELECT *
FROM mail_auth_info
WHERE email = #{email}

mapper.update:
UPDATE mail_auth_info
SET
	auth_code = #{auth_code}
WHERE
	email = #{email}

UPDATE member
SET 
	mem_name = #{mem_name}
	,mem_passwd = #{mem_passwd}
	,mem_post_code = #{mem_post_code}
	,mem_add1 = #{mem_add1}
	,mem_add2 = #{mem_add2}
	,mem_tel = #{mem_tel}
	,mem_birthday = #{mem_birthday}
WHERE
	mem_email = #{mem_email}

mapper.select:
SELECT COUNT(*)
FROM follow
WHERE follow_creator = #{mem_email}

SELECT *
FROM product_info p
JOIN like_ l
ON l.like_product_code = p.product_code
WHERE l.like_mem_email = #{mem_email}
AND l.like_status = 'Y'
LIMIT
	#{startRow}     
	, #{listLimit}

mapper.update:
UPDATE like_
SET like_status = 'N'
WHERE 
	like_project_code = #{like_project_code}
	AND like_mem_email = #{like_mem_email}
	AND like_status = 'Y'

UPDATE like_
SET like_status = 'N'
WHERE 
	like_product_code = #{like_product_code}
	AND like_mem_email = #{like_mem_email}
	AND like_status = 'Y'

mapper.select:
SELECT creator_name
FROM creator_info
WHERE creator_email = #{creatorEmail}

SELECT *
FROM member
WHERE mem_email = #{mem_email}

SELECT mem_email
FROM member
WHERE mem_email = #{receiver_id}

