mapper.select:
SELECT COUNT(*)
FROM follow
WHERE follow_mem_email = #{creator_email}

SELECT COUNT(*)
FROM user_address
WHERE address_mem_email = #{id}
AND address_is_default = 'Y';

mapper.update:
UPDATE user_address
SET address_is_default = 'N'
WHERE address_mem_email = #{id}
AND address_is_default = 'Y';
